query getVorgang($vorgangsnummer: String!) {
    vorgang(vorgangsnummer: $vorgangsnummer) {
        vorgangsnummer
        kundenbetreuer {
            partnerId
        }
        antragsteller1 {
            personendaten {
                vorname
                nachname
           }
        }
        antragsteller2 {
            personendaten {
                vorname
                nachname
            }
        }
        antraege {
            produktanbieterstatus {
                status
            }
        }
    }
}
